import { useState } from "react";

function AddClassForm({ onAdd }) {
  const [grade, setGrade] = useState("");
  const [teacher, setTeacher] = useState(""); // type the teacher
  const [subject, setSubject] = useState(""); // type the subject
  const [fee, setFee] = useState("");
  const [timetable, setTimetable] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!grade || !teacher || !subject) return;

    // Prepare the class object
    const newClass = {
      grade,
      fee: parseFloat(fee),
      timetable,
      // for simplicity we send teacher and subject as string
      teacher: { name: teacher },
      subjects: [{ name: subject }]
    };

    onAdd(newClass);

    // Reset form
    setGrade("");
    setTeacher("");
    setSubject("");
    setFee("");
    setTimetable("");
  };

  return (
    <form onSubmit={handleSubmit} className="mb-6 bg-white p-4 rounded shadow">
      <div className="mb-2">
        <label className="block font-bold">Grade:</label>
        <input
          type="text"
          value={grade}
          onChange={(e) => setGrade(e.target.value)}
          className="border p-1 w-full"
          required
        />
      </div>
      <div className="mb-2">
        <label className="block font-bold">Teacher:</label>
        <input
          type="text"
          value={teacher}
          onChange={(e) => setTeacher(e.target.value)}
          className="border p-1 w-full"
          required
        />
      </div>
      <div className="mb-2">
        <label className="block font-bold">Subject:</label>
        <input
          type="text"
          value={subject}
          onChange={(e) => setSubject(e.target.value)}
          className="border p-1 w-full"
          required
        />
      </div>
      <div className="mb-2">
        <label className="block font-bold">Fee:</label>
        <input
          type="number"
          value={fee}
          onChange={(e) => setFee(e.target.value)}
          className="border p-1 w-full"
        />
      </div>
      <div className="mb-2">
        <label className="block font-bold">Timetable:</label>
        <input
          type="text"
          value={timetable}
          onChange={(e) => setTimetable(e.target.value)}
          placeholder="Monday 10:00-11:00"
          className="border p-1 w-full"
        />
      </div>
      <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">
        Add Class
      </button>
    </form>
  );
}

export default AddClassForm;
