import { useState, useEffect } from "react";
import axios from "axios";
import ClassCard from "../components/ClassCard";
import SearchBar from "../components/SearchBar";

function BrowseClassesPage() {
  const [classes, setClasses] = useState([]);

  // Load all classes on page load
  useEffect(() => {
    axios.get("http://localhost:8080/classes")
      .then(res => setClasses(res.data))
      .catch(err => console.error(err));
  }, []);

  // Handle search from SearchBar component
  const handleSearch = (query) => {
    axios.get(`http://localhost:8080/classes/search?subject=${query}`)
      .then(res => setClasses(res.data))
      .catch(err => console.error(err));
  };

  return (
    <div>
      <h1>Browse Classes</h1>
      <SearchBar onSearch={handleSearch} />
      
      <div className="class-list">
        {classes.length === 0 ? (
          <p>No classes found</p>
        ) : (
          classes.map(cls => <ClassCard key={cls.id} cls={cls} />)
        )}
      </div>
    </div>
  );
}

export default BrowseClassesPage;
