// src/pages/ClassList.js
import React, { useState, useEffect } from "react";
import ClassCard from "../components/ClassCard";
import SearchBar from "../components/SearchBar";

const ClassList = () => {
  const [classes, setClasses] = useState([]);

  const fetchClasses = async (subject = "") => {
    let url = "http://localhost:8080/classes";
    if (subject) {
      url += `/search?subject=${subject}`;
    }
    const response = await fetch(url);
    const data = await response.json();
    setClasses(data);
  };

  useEffect(() => {
    fetchClasses();
  }, []);

  const handleCardClick = (cls) => {
    console.log("Clicked class:", cls);
    // Here you can navigate to a detail page if needed
  };

  return (
    <div>
      <SearchBar onSearch={fetchClasses} />
      <div className="cards-container">
        {classes.map((cls) => (
          <ClassCard key={cls.id} cls={cls} onClick={handleCardClick} />
        ))}
      </div>
    </div>
  );
};

export default ClassList;
