// src/pages/ClassList.js
import React, { useState, useEffect } from "react";
import ClassCard from "../components/ClassCard";
import SearchBar from "../components/SearchBar";

const ClassList = () => {
  const [classes, setClasses] = useState([]);

  const fetchClasses = async (subject = "") => {
    try {
      let url = "http://localhost:8080/classes";
      if (subject) url += `/search?subject=${encodeURIComponent(subject)}`;

      const response = await fetch(url);
      const data = await response.json();
      setClasses(data);
    } catch (error) {
      console.error("Failed to fetch classes:", error);
      setClasses([]);
    }
  };

  useEffect(() => { fetchClasses(); }, []);

  return (
    <div>
      <SearchBar onSearch={fetchClasses} />
      <div className="class-list">
        {classes.length === 0 ? (
          <p>No classes found.</p>
        ) : (
          classes.map(cls => <ClassCard key={cls.id} classData={cls} />)
        )}
      </div>
    </div>
  );
};

export default ClassList;
