import React, { useState } from "react";

function AddClassForm({ onAdd }) {
  const [grade, setGrade] = useState("");
  const [fee, setFee] = useState("");
  const [timetable, setTimetable] = useState("");
  const [teacherId, setTeacherId] = useState("");
  const [subjectIds, setSubjectIds] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const newClass = {
      grade,
      fee: parseFloat(fee),
      timetable,
      teacher: { id: parseInt(teacherId) },   // teacher must exist
      subjects: subjectIds.split(",").map(id => ({ id: parseInt(id) })) // subjects must exist
    };
    onAdd(newClass);
    setGrade("");
    setFee("");
    setTimetable("");
    setTeacherId("");
    setSubjectIds("");
  };

  return (
    <form onSubmit={handleSubmit} className="p-4 bg-gray-100 rounded mb-6">
      <h2 className="text-lg font-bold mb-4">Add New Class</h2>

      <input
        type="text"
        placeholder="Grade"
        value={grade}
        onChange={(e) => setGrade(e.target.value)}
        className="border p-2 mr-2 mb-2"
        required
      />

      <input
        type="number"
        placeholder="Fee"
        value={fee}
        onChange={(e) => setFee(e.target.value)}
        className="border p-2 mr-2 mb-2"
        required
      />

      <input
        type="text"
        placeholder="Timetable"
        value={timetable}
        onChange={(e) => setTimetable(e.target.value)}
        className="border p-2 mr-2 mb-2"
        required
      />

      <input
        type="number"
        placeholder="Teacher ID"
        value={teacherId}
        onChange={(e) => setTeacherId(e.target.value)}
        className="border p-2 mr-2 mb-2"
        required
      />

      <input
        type="text"
        placeholder="Subject IDs (comma separated)"
        value={subjectIds}
        onChange={(e) => setSubjectIds(e.target.value)}
        className="border p-2 mr-2 mb-2"
        required
      />

      <button
        type="submit"
        className="bg-blue-500 text-white px-4 py-2 rounded"
      >
        Add Class
      </button>
    </form>
  );
}

export default AddClassForm;
