import React, { useState, useEffect } from "react";
import axios from "axios";

function AddClassForm({ onAdd }) {
  const [grade, setGrade] = useState("");
  const [fee, setFee] = useState("");
  const [timetable, setTimetable] = useState("");
  const [teacherId, setTeacherId] = useState("");
  const [subjectIds, setSubjectIds] = useState([]);

  const [teachers, setTeachers] = useState([]);
  const [subjects, setSubjects] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:8080/teachers").then(res => setTeachers(res.data));
    axios.get("http://localhost:8080/subjects").then(res => setSubjects(res.data));
  }, []);


  const handleSubmit = async (e) => {
    e.preventDefault();

    const newClass = {
      grade,
      fee: parseFloat(fee),
      timetable,
      teacher: { id: parseInt(teacherId)},
      subjects: subjectIds.map(id=>({id: parseInt(id) }))
    };

    await axios.post("http://localhost:8080/classes", newClass);
    onAdd();
  };

  return (
    <form onSubmit={handleSubmit} className="p-4 bg-gray-100 rounded">
      <input value={grade} onChange={e => setGrade(e.target.value)} placeholder="Grade" className="border p-2 mr-2" />
      <input value={fee} onChange={e => setFee(e.target.value)} placeholder="Fee" type="number" className="border p-2 mr-2" />
      <input value={timetable} onChange={e => setTimetable(e.target.value)} placeholder="Timetable" className="border p-2 mr-2" />

      {/* Teacher dropdown */}
      <select value={teacherId} onChange={e => setTeacherId(e.target.value)} className="border p-2 mr-2">
        <option value="">Select Teacher</option>
        {teachers.map(t => (
          <option key={t.id} value={t.id}>{t.name}</option>
        ))}
      </select>

      {/* Subjects multi-select */}
      <select multiple value={subjectIds} onChange={e => setSubjectIds([...e.target.selectedOptions].map(o => o.value))} className="border p-2 mr-2">
        {subjects.map(s => (
          <option key={s.id} value={s.id}>{s.name}</option>
        ))}
      </select>

      <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">Add Class</button>
    </form>
  );
}

export default AddClassForm;
