import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";

function ClassDetailsPage() {
  const { id } = useParams();
  const [cls, setCls] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:8080/classes/${id}`)
      .then(res => setCls(res.data))
      .catch(err => console.error(err));
  }, [id]);

  if (!cls) return <p className="p-6">Loading...</p>;

  return (
    <div className="p-6 max-w-4xl mx-auto bg-white shadow rounded-lg">
      <h1 className="text-3xl font-bold mb-2">{cls.subjects.map(s => s.name).join(", ")}</h1>
      <p className="mb-1">Grade: {cls.grade}</p>
      <p className="mb-1">Fee: Rs. {cls.fee}</p>
      <p className="mb-1">Teacher: {cls.teacher?.name}</p>
      {/* TODO: Add timetable, description, and extra info */}
    </div>
  );
}

export default ClassDetailsPage;
