// src/pages/AdminClassList.js
import React, { useState, useEffect } from "react";
import ClassCard from "../components/ClassCard";
import SearchBar from "../components/SearchBar";

const AdminClassList = () => {
  const [classes, setClasses] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [currentClass, setCurrentClass] = useState(null); // null for new class
  const [formData, setFormData] = useState({
    subject: "",
    grade: "",
    teacher: "",
    fee: ""
  });

  // Fetch all classes or search by subject
  const fetchClasses = async (subject = "") => {
    try {
      let url = "http://localhost:8080/classes";
      if (subject) {
        url += `/search?subject=${encodeURIComponent(subject)}`;
      }
      const response = await fetch(url);
      const data = await response.json();
      setClasses(data);
    } catch (error) {
      console.error("Failed to fetch classes:", error);
      setClasses([]);
    }
  };

  useEffect(() => {
    fetchClasses();
  }, []);

  // Handle input changes in form
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Open form for adding new class
  const handleAdd = () => {
    setCurrentClass(null);
    setFormData({ subject: "", grade: "", teacher: "", fee: "" });
    setShowForm(true);
  };

  // Open form for editing existing class
  const handleEdit = (cls) => {
    setCurrentClass(cls);
    setFormData({
      subject: cls.subject,
      grade: cls.grade,
      teacher: cls.teacher,
      fee: cls.fee
    });
    setShowForm(true);
  };

  // Delete class
  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this class?")) {
      await fetch(`http://localhost:8080/classes/${id}`, { method: "DELETE" });
      fetchClasses();
    }
  };

  // Submit form (add or edit)
  const handleSubmit = async (e) => {
    e.preventDefault();
    const method = currentClass ? "PUT" : "POST";
    const url = currentClass
      ? `http://localhost:8080/classes/${currentClass.id}`
      : "http://localhost:8080/classes";

    await fetch(url, {
      method,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        subject: formData.subject,
        grade: formData.grade,
        teacher: formData.teacher,
        fee: parseFloat(formData.fee)
      })
    });

    setShowForm(false);
    fetchClasses();
  };

  return (
    <div className="admin-class-list">
      <h1>Admin Class Dashboard</h1>
      <SearchBar onSearch={fetchClasses} />

      <button onClick={handleAdd} style={{ margin: "10px 0" }}>
        Add New Class
      </button>

      {/* Display class cards with Edit/Delete buttons */}
      <div className="class-list">
        {classes.length === 0 ? (
          <p>No classes found.</p>
        ) : (
          classes.map((cls) => (
            <div key={cls.id} className="class-card-container">
              <ClassCard classData={cls} />
              <button onClick={() => handleEdit(cls)}>Edit</button>
              <button onClick={() => handleDelete(cls.id)}>Delete</button>
            </div>
          ))
        )}
      </div>

      {/* Form modal */}
      {showForm && (
        <div className="form-modal">
          <h2>{currentClass ? "Edit Class" : "Add Class"}</h2>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              name="subject"
              placeholder="Subject"
              value={formData.subject}
              onChange={handleChange}
              required
            />
            <input
              type="text"
              name="grade"
              placeholder="Grade"
              value={formData.grade}
              onChange={handleChange}
              required
            />
            <input
              type="text"
              name="teacher"
              placeholder="Teacher"
              value={formData.teacher}
              onChange={handleChange}
              required
            />
            <input
              type="number"
              name="fee"
              placeholder="Fee"
              value={formData.fee}
              onChange={handleChange}
              required
            />
            <button type="submit">Save</button>
            <button type="button" onClick={() => setShowForm(false)}>
              Cancel
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default AdminClassList;
