import React, { useState } from "react";

function AddClassForm({ onAdd, teachers, subjects }) {
  const [grade, setGrade] = useState("");
  const [fee, setFee] = useState("");
  const [timetable, setTimetable] = useState("");
  const [teacherId, setTeacherId] = useState("");
  const [subjectIds, setSubjectIds] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const newClass = {
      grade,
      fee: parseFloat(fee),
      timetable,
      teacher: { id: parseInt(teacherId) },
      subjects: subjectIds.map(id => ({ id: parseInt(id) }))
    };
    onAdd(newClass);

    // reset form
    setGrade(""); setFee(""); setTimetable(""); setTeacherId(""); setSubjectIds([]);
  };

  const handleSubjectChange = (e) => {
    const options = e.target.options;
    const selected = [];
    for (let i = 0; i < options.length; i++) {
      if (options[i].selected) selected.push(options[i].value);
    }
    setSubjectIds(selected);
  };

  return (
    <form onSubmit={handleSubmit} className="p-4 bg-gray-100 rounded mb-6">
      <h2 className="text-lg font-bold mb-4">Add New Class</h2>

      <input type="text" placeholder="Grade" value={grade} onChange={e => setGrade(e.target.value)} className="border p-2 mr-2 mb-2" required />
      <input type="number" placeholder="Fee" value={fee} onChange={e => setFee(e.target.value)} className="border p-2 mr-2 mb-2" required />
      <input type="text" placeholder="Timetable" value={timetable} onChange={e => setTimetable(e.target.value)} className="border p-2 mr-2 mb-2" required />

      <select value={teacherId} onChange={e => setTeacherId(e.target.value)} className="border p-2 mr-2 mb-2" required>
        <option value="">Select Teacher</option>
        {teachers.map(t => <option key={t.teacher_id} value={t.teacher_id}>{t.name}</option>)}
      </select>

      <select multiple value={subjectIds} onChange={handleSubjectChange} className="border p-2 mr-2 mb-2">
        {subjects.map(s => <option key={s.subject_id} value={s.subject_id}>{s.name}</option>)}
      </select>

      <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">Add Class</button>
    </form>
  );
}

export default AddClassForm;
