const ClassTable = ({ classes, onEdit, onDelete }) => {
  return (
    <table border="1" cellPadding="8" cellSpacing="0">
      <thead>
        <tr>
          <th>Grade</th>
          <th>Teacher</th>
          <th>Fee</th>
          <th>Subjects</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {classes.map((cls) => (
          <tr key={cls.id}>
            <td>{cls.grade}</td>
            <td>{cls.teacher}</td>
            <td>{cls.fee}</td>
            <td>{cls.subjects.map((s) => s.name).join(", ")}</td>
            <td>
              <button onClick={() => onEdit(cls.id)}>Edit</button>
              <button onClick={() => onDelete(cls.id)}>Delete</button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default ClassTable;
