import React, { useEffect, useState } from "react";

const API_URL = "http://localhost:8080/classes";

const AdminClassList = () => {
  const [classes, setClasses] = useState([]);
  const [form, setForm] = useState({ subject: "", grade: "", teacher: "", fee: "" });
  const [editingId, setEditingId] = useState(null);

  // Fetch all classes
  const fetchClasses = async () => {
    try {
      const res = await fetch(API_URL);
      const data = await res.json();
      setClasses(data);
    } catch (err) {
      console.error("Error fetching classes:", err);
    }
  };

  useEffect(() => {
    fetchClasses();
  }, []);

  // Handle form change
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Add new class
  const handleAdd = async (e) => {
    e.preventDefault();
    try {
      await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
      setForm({ subject: "", grade: "", teacher: "", fee: "" });
      fetchClasses();
    } catch (err) {
      console.error("Error adding class:", err);
    }
  };

  // Edit existing class
  const handleEdit = (cls) => {
    setForm(cls);
    setEditingId(cls.id);
  };

  // Update class
  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      await fetch(`${API_URL}/${editingId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
      setForm({ subject: "", grade: "", teacher: "", fee: "" });
      setEditingId(null);
      fetchClasses();
    } catch (err) {
      console.error("Error updating class:", err);
    }
  };

  // Delete class
  const handleDelete = async (id) => {
    try {
      await fetch(`${API_URL}/${id}`, { method: "DELETE" });
      fetchClasses();
    } catch (err) {
      console.error("Error deleting class:", err);
    }
  };

  return (
    <div className="p-6 bg-white min-h-screen">
      <h1 className="text-2xl font-bold mb-4">Manage Classes</h1>

      {/* Form */}
      <form onSubmit={editingId ? handleUpdate : handleAdd} className="mb-6 space-x-2">
        <input
          type="text"
          name="subject"
          value={form.subject}
          onChange={handleChange}
          placeholder="Subject"
          className="border px-3 py-2 rounded"
          required
        />
        <input
          type="text"
          name="grade"
          value={form.grade}
          onChange={handleChange}
          placeholder="Grade"
          className="border px-3 py-2 rounded"
          required
        />
        <input
          type="text"
          name="teacher"
          value={form.teacher}
          onChange={handleChange}
          placeholder="Teacher"
          className="border px-3 py-2 rounded"
          required
        />
        <input
          type="number"
          name="fee"
          value={form.fee}
          onChange={handleChange}
          placeholder="Fee"
          className="border px-3 py-2 rounded"
          required
        />
        <button
          type="submit"
          className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
        >
          {editingId ? "Update Class" : "Add Class"}
        </button>
      </form>

      {/* Table */}
      <table className="w-full border-collapse border">
        <thead>
          <tr className="bg-gray-200">
            <th className="border px-4 py-2">Subject</th>
            <th className="border px-4 py-2">Grade</th>
            <th className="border px-4 py-2">Teacher</th>
            <th className="border px-4 py-2">Fee</th>
            <th className="border px-4 py-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {classes.map((cls) => (
            <tr key={cls.id}>
              <td className="border px-4 py-2">{cls.subject}</td>
              <td className="border px-4 py-2">{cls.grade}</td>
              <td className="border px-4 py-2">{cls.teacher}</td>
              <td className="border px-4 py-2">{cls.fee}</td>
              <td className="border px-4 py-2 space-x-2">
                <button
                  onClick={() => handleEdit(cls)}
                  className="bg-yellow-500 text-white px-3 py-1 rounded hover:bg-yellow-600"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(cls.id)}
                  className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
          {classes.length === 0 && (
            <tr>
              <td colSpan="5" className="text-center py-4">
                No classes found
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default AdminClassList;
