import React, { useEffect, useState } from "react";

const API_URL = "http://localhost:8080/classes";

const ClassList = () => {
  const [classes, setClasses] = useState([]);

  useEffect(() => {
    fetch(API_URL)
      .then((res) => res.json())
      .then((data) => setClasses(data))
      .catch((err) => console.error("Error fetching classes:", err));
  }, []);

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-2xl font-bold mb-4">Available Classes</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {classes.map((cls) => (
          <div key={cls.id} className="bg-white shadow rounded-lg p-4">
            <h2 className="text-lg font-semibold">{cls.subject}</h2>
            <p>Grade: {cls.grade}</p>
            <p>Teacher: {cls.teacher}</p>
            <p>Fee: Rs. {cls.fee}</p>
          </div>
        ))}
        {classes.length === 0 && (
          <p className="col-span-full text-center text-gray-500">
            No classes available
          </p>
        )}
      </div>
    </div>
  );
};

export default ClassList;
