import { useState } from "react";

function AddClassForm({ onAdd }) {
  const [formData, setFormData] = useState({
    subject: "",
    grade: "",
    teacher: "",
    fee: ""
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onAdd(formData);
    setFormData({ subject: "", grade: "", teacher: "", fee: "" });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="subject" value={formData.subject} onChange={handleChange} placeholder="Subject" />
      <input name="grade" value={formData.grade} onChange={handleChange} placeholder="Grade" />
      <input name="teacher" value={formData.teacher} onChange={handleChange} placeholder="Teacher" />
      <input name="fee" value={formData.fee} onChange={handleChange} placeholder="Fee" />
      <button type="submit">Add Class</button>
    </form>
  );
}

export default AddClassForm;
