import React, { useEffect, useState } from "react";
import axios from "axios";
import AddClassForm from "../components/AddClassForm";
import ClassTable from "../components/ClassTable";

function ClassRecordsPage() {
  const [classes, setClasses] = useState([]);
  const [teachers, setTeachers] = useState([]);
  const [subjects, setSubjects] = useState([]);

  useEffect(() => {
    fetchClasses();
    fetchTeachers();
    fetchSubjects();
  }, []);

  const fetchClasses = () => {
    axios.get("http://localhost:8080/classes")
      .then(res => setClasses(res.data))
      .catch(err => console.error(err));
  };

  const fetchTeachers = () => {
    axios.get("http://localhost:8080/teachers")
      .then(res => setTeachers(res.data))
      .catch(err => console.error(err));
  };

  const fetchSubjects = () => {
    axios.get("http://localhost:8080/subjects")
      .then(res => setSubjects(res.data))
      .catch(err => console.error(err));
  };

  const handleAddClass = (newClass) => {
    axios.post("http://localhost:8080/classes", newClass)
      .then(res => setClasses(prev => [...prev, res.data]))
      .catch(err => console.error(err));
  };

  const handleEdit = (cls) => {
    const updatedGrade = prompt("Enter new grade", cls.grade);
    if (!updatedGrade) return;

    const updatedClass = { ...cls, grade: updatedGrade };
    axios.put(`http://localhost:8080/classes/${cls.class_id}`, updatedClass)
      .then(res => setClasses(prev => prev.map(c => c.class_id === cls.class_id ? res.data : c)))
      .catch(err => console.error(err));
  };

  const handleDelete = (id) => {
    axios.delete(`http://localhost:8080/classes/${id}`)
      .then(() => setClasses(prev => prev.filter(c => c.class_id !== id)))
      .catch(err => console.error(err));
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Class Records</h1>
      <AddClassForm onAdd={handleAddClass} teachers={teachers} subjects={subjects} />
      <ClassTable classes={classes} onEdit={handleEdit} onDelete={handleDelete} />
    </div>
  );
}

export default ClassRecordsPage;
