import { Link } from "react-router-dom";

function ClassCard({ cls }) {
  return (
    <Link to={`/classes/${cls.id}`}>
      <div className="bg-white shadow rounded-lg p-4 hover:shadow-lg transition transform hover:scale-105">
        <h3 className="text-lg font-bold mb-1">{cls.subjects.map(s => s.name).join(", ")}</h3>
        <p className="text-gray-600">Grade: {cls.grade}</p>
        <p className="text-gray-600">Teacher: {cls.teacher?.name}</p>
        <p className="text-gray-800 font-semibold mt-2">Fee: Rs.{cls.fee}</p>
      </div>
    </Link>
  );
}

export default ClassCard;
