import { useState, useEffect } from "react";
import axios from "axios";
import AddClassForm from "../components/AddClassForm";
import ClassTable from "../components/ClassTable";

function ClassRecordsPage() {
  const [classes, setClasses] = useState([]);

  // Fetch all classes from backend
  const fetchClasses = () => {
    axios
      .get("http://localhost:8080/classes")
      .then((res) => setClasses(res.data))
      .catch((err) => console.error(err));
  };

  useEffect(() => {
    fetchClasses();
  }, []);

  // Add new class
  const handleAddClass = (newClass) => {
    axios
      .post("http://localhost:8080/classes", newClass)
      .then(() => fetchClasses())
      .catch((err) => console.error(err));
  };

  // Edit existing class
  const handleEditClass = (id, updatedClass) => {
    axios
      .put(`http://localhost:8080/classes/${id}`, updatedClass)
      .then(() => fetchClasses())
      .catch((err) => console.error(err));
  };

  // Delete a class
  const handleDeleteClass = (id) => {
    axios
      .delete(`http://localhost:8080/classes/${id}`)
      .then(() => fetchClasses())
      .catch((err) => console.error(err));
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-center">Class Records</h1>

      {/* Form to add new class */}
      <AddClassForm onAdd={handleAddClass} />

      {/* Table showing all classes with edit/delete */}
      <ClassTable
        classes={classes}
        onEdit={handleEditClass}
        onDelete={handleDeleteClass}
      />
    </div>
  );
}

export default ClassRecordsPage;
