import React, { useState, useEffect } from "react";

function AddClassForm({ onClassAdded }) {
  const [grade, setGrade] = useState("");
  const [fee, setFee] = useState("");
  const [timetable, setTimetable] = useState("");
  const [teacherId, setTeacherId] = useState("");
  const [subjectIds, setSubjectIds] = useState([]);

  const [teachers, setTeachers] = useState([]);
  const [subjects, setSubjects] = useState([]);

  // Fetch teachers and subjects for dropdowns
  useEffect(() => {
    fetch("http://localhost:8080/teachers")
      .then(res => res.json())
      .then(data => setTeachers(data))
      .catch(err => console.error("Error fetching teachers:", err));

    fetch("http://localhost:8080/subjects")
      .then(res => res.json())
      .then(data => setSubjects(data))
      .catch(err => console.error("Error fetching subjects:", err));
  }, []);

  // Handle subject checkbox
  const handleSubjectChange = (id) => {
    setSubjectIds(prev =>
      prev.includes(id) ? prev.filter(s => s !== id) : [...prev, id]
    );
  };

  // Submit form
  const handleSubmit = (e) => {
    e.preventDefault();

    const newClass = {
      grade,
      fee: parseFloat(fee),
      timetable,
      teacher: { id: parseInt(teacherId) },
      subjects: subjectIds.map(id => ({ id }))
    };

    fetch("http://localhost:8080/classes", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newClass)
    })
      .then(res => res.json())
      .then(data => {
        onClassAdded(data); // update parent state
        setGrade("");
        setFee("");
        setTimetable("");
        setTeacherId("");
        setSubjectIds([]);
      })
      .catch(err => console.error("Error adding class:", err));
  };

  return (
    <form onSubmit={handleSubmit} className="p-4 border rounded bg-gray-100 mb-4">
      <h2 className="text-lg font-bold mb-2">Add New Class</h2>

      <div className="mb-2">
        <label className="block">Grade:</label>
        <input
          type="text"
          value={grade}
          onChange={(e) => setGrade(e.target.value)}
          className="border px-2 py-1 w-full"
          required
        />
      </div>

      <div className="mb-2">
        <label className="block">Fee:</label>
        <input
          type="number"
          value={fee}
          onChange={(e) => setFee(e.target.value)}
          className="border px-2 py-1 w-full"
          required
        />
      </div>

      <div className="mb-2">
        <label className="block">Timetable:</label>
        <input
          type="text"
          value={timetable}
          onChange={(e) => setTimetable(e.target.value)}
          className="border px-2 py-1 w-full"
          required
        />
      </div>

      <div className="mb-2">
        <label className="block">Teacher:</label>
        <select
          value={teacherId}
          onChange={(e) => setTeacherId(e.target.value)}
          className="border px-2 py-1 w-full"
          required
        >
          <option value="">-- Select Teacher --</option>
          {teachers.map(t => (
            <option key={t.id} value={t.id}>
              {t.name}
            </option>
          ))}
        </select>
      </div>

      <div className="mb-2">
        <label className="block">Subjects:</label>
        {subjects.map(s => (
          <div key={s.id} className="flex items-center">
            <input
              type="checkbox"
              checked={subjectIds.includes(s.id)}
              onChange={() => handleSubjectChange(s.id)}
            />
            <span className="ml-2">{s.name}</span>
          </div>
        ))}
      </div>

      <button
        type="submit"
        className="bg-green-500 text-white px-4 py-2 rounded"
      >
        Add Class
      </button>
    </form>
  );
}

export default AddClassForm;
