import React, { useEffect, useState } from "react";
import AddClassForm from "../components/AddClassForm";
import ClassTable from "../components/ClassTable";

function ClassRecords() {
  const [classes, setClasses] = useState([]);

  useEffect(() => {
    // TEMPORARY: hardcoded data to test table rendering
    setClasses([
      {
        class_id: 1,
        grade: "Grade 10",
        fee: 5000,
        timetable: "Mon-Wed-Fri",
        teacher: { name: "Mr. Silva" },
        subjects: [{ name: "Mathematics" }]
      }
    ]);
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Class Records</h1>
      <AddClassForm onAdd={(newClass) => console.log(newClass)} />
      <ClassTable classes={classes} onEdit={() => {}} onDelete={() => {}} />
    </div>
  );
}

export default ClassRecords;
