import { useState, useEffect } from "react";
import axios from "axios";

function AddClassForm({ onAdd }) {
  const [grade, setGrade] = useState("");
  const [fee, setFee] = useState("");
  const [teacherId, setTeacherId] = useState("");
  const [subjects, setSubjects] = useState([]);
  const [selectedSubjects, setSelectedSubjects] = useState([]);
  const [timetable, setTimetable] = useState("");

  const [allTeachers, setAllTeachers] = useState([]);
  const [allSubjects, setAllSubjects] = useState([]);

  useEffect(() => {
    // Fetch teachers
    axios.get("http://localhost:8080/teachers")
      .then(res => setAllTeachers(res.data))
      .catch(err => console.error(err));

    // Fetch subjects
    axios.get("http://localhost:8080/subjects")
      .then(res => setAllSubjects(res.data))
      .catch(err => console.error(err));
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    const newClass = {
      grade,
      fee: parseFloat(fee),
      teacher: { id: parseInt(teacherId) },
      subjects: selectedSubjects.map(id => ({ id: parseInt(id) })),
      timetable
    };

    onAdd(newClass);

    // Reset form
    setGrade("");
    setFee("");
    setTeacherId("");
    setSelectedSubjects([]);
    setTimetable("");
  };

  return (
    <form onSubmit={handleSubmit} className="mb-6 p-4 bg-white shadow rounded">
      <h2 className="text-xl font-bold mb-4">Add New Class</h2>

      <div className="mb-2">
        <label className="block">Grade:</label>
        <input value={grade} onChange={e => setGrade(e.target.value)} className="border p-2 w-full"/>
      </div>

      <div className="mb-2">
        <label className="block">Fee:</label>
        <input type="number" value={fee} onChange={e => setFee(e.target.value)} className="border p-2 w-full"/>
      </div>

      <div className="mb-2">
        <label className="block">Teacher:</label>
        <select value={teacherId} onChange={e => setTeacherId(e.target.value)} className="border p-2 w-full">
          <option value="">Select Teacher</option>
          {allTeachers.map(t => (
            <option key={t.id} value={t.id}>{t.name}</option>
          ))}
        </select>
      </div>

      <div className="mb-2">
        <label className="block">Subjects:</label>
        <select multiple value={selectedSubjects} onChange={e => {
          const options = Array.from(e.target.selectedOptions).map(opt => opt.value);
          setSelectedSubjects(options);
        }} className="border p-2 w-full h-24">
          {allSubjects.map(s => (
            <option key={s.id} value={s.id}>{s.name}</option>
          ))}
        </select>
      </div>

      <div className="mb-2">
        <label className="block">Timetable:</label>
        <input value={timetable} onChange={e => setTimetable(e.target.value)} className="border p-2 w-full"/>
      </div>

      <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">Add Class</button>
    </form>
  );
}

export default AddClassForm;
