import { useEffect, useState } from "react";
import axios from "axios";
import ClassCard from "../components/ClassCard";
import SearchBar from "../components/SearchBar";

function BrowseClassesPage() {
  const [classes, setClasses] = useState([]);
  const [query, setQuery] = useState("");

  const fetchClasses = () => {
    axios.get(`http://localhost:8080/classes${query ? `/search?subject=${query}` : ""}`)
      .then(res => setClasses(res.data))
      .catch(err => console.error(err));
  };

  useEffect(() => {
    fetchClasses();
  }, [query]);

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <h1 className="text-3xl font-bold mb-4">Browse Classes</h1>
      <SearchBar onSearch={setQuery} />
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {classes.length === 0 ? (
          <p className="col-span-full text-center text-gray-500">No classes found</p>
        ) : (
          classes.map(cls => <ClassCard key={cls.id} cls={cls} />)
        )}
      </div>
    </div>
  );
}

export default BrowseClassesPage;
